import React, {Component, useState, useEffect} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import {AppCss} from './App.js'
import Header from './components/header/header';
import HomePage from './components/page/homePage';
import ExtraPage from './components/page/extraPage';
import DataBasePage from './components/page/dataBasePage';
import Layout from './components/layout/layout.js';


function useKey(key) {
  // Keep track of key state
  const [pressed, setPressed] = useState(false)

  // Does an event match the key we're watching?
  const match = event => key.toLowerCase() == event.key.toLowerCase()

  // Event handlers
  const onDown = event => {
      if (match(event)) setPressed(true)
  }

  const onUp = event => {
      if (match(event)) setPressed(false)
  }

  // Bind and unbind events
  useEffect(() => {
      window.addEventListener("keydown", onDown)
      window.addEventListener("keyup", onUp)
      return () => {
      window.removeEventListener("keydown", onDown)
      window.removeEventListener("keyup", onUp)
      }
  }, [key])

  return pressed
}

class App extends Component {

  render(){
    const shift = useKey('shift')
    return (
      <React.Fragment>
        <Header />
        <Layout>
          <Router>
            <Switch>
              <Route exact path="/" component={HomePage}/>
              <Route exact path="/extra" component={ExtraPage}/>
              <Route exact path="/database" component={DataBasePage}/>
            </Switch>  
          </Router>
        </Layout>
      </React.Fragment>
    );
  }
}

export default App;
